    // Code for dispatching events from components to event handlers.

    private class FormListener implements java.awt.event.ActionListener, java.awt.event.ItemListener {
        FormListener() {}
        public void actionPerformed(java.awt.event.ActionEvent evt) {
            if (evt.getSource() == saveButton) {
                TitulosJanela.this.saveButtonActionPerformed(evt);
            }
            else if (evt.getSource() == refreshButton) {
                TitulosJanela.this.refreshButtonActionPerformed(evt);
            }
            else if (evt.getSource() == newButton) {
                TitulosJanela.this.newButtonActionPerformed(evt);
            }
            else if (evt.getSource() == deleteButton) {
                TitulosJanela.this.deleteButtonActionPerformed(evt);
            }
        }

        public void itemStateChanged(java.awt.event.ItemEvent evt) {
            if (evt.getSource() == cartaoJChekBox) {
                TitulosJanela.this.cartaoJChekBoxItemStateChanged(evt);
            }
        }
    }// </editor-fold>//GEN-END:initComponents

    @SuppressWarnings("unchecked")
    private void refreshButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_refreshButtonActionPerformed
        br.sql.acesso.ConnectionFactory.cancelar();
        java.util.Collection data = query.getResultList();
        data.forEach((entity) -> {
            br.sql.acesso.ConnectionFactory.getEntityManagerNew().refresh(entity);
        });
        list.clear();
        list.addAll(data);
    }//GEN-LAST:event_refreshButtonActionPerformed

    private void deleteButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deleteButtonActionPerformed
        int[] selected = masterTable.getSelectedRows();
        List<JsysTitulos> toRemove = new ArrayList<>(selected.length);
        for (int idx = 0; idx < selected.length; idx++) {
            JsysTitulos t = list.get(masterTable.convertRowIndexToModel(selected[idx]));
            toRemove.add(t);
            br.sql.acesso.ConnectionFactory.delete(t);
        }
        list.removeAll(toRemove);
    }//GEN-LAST:event_deleteButtonActionPerformed

    private void newButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_newButtonActionPerformed
        JsysTitulos t = new JsysTitulos();
        list.add(t);
        int row = list.size() - 1;
        masterTable.setRowSelectionInterval(row, row);
        masterTable.scrollRectToVisible(masterTable.getCellRect(row, 0, true));
    }//GEN-LAST:event_newButtonActionPerformed

    private void saveButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveButtonActionPerformed
        if (!cnpjPreatadoraJtextField.getText().isEmpty() & !Validar.cnpj(cnpjPreatadoraJtextField.getText())) {
            JOptionPane.showMessageDialog(this, "CNPJ invalido. Verifique o valor informado no Campo. ");
            cnpjPreatadoraJtextField.requestFocus();
        } else {
            int[] selected = masterTable.getSelectedRows();
            for (int idx = 0; idx < selected.length; idx++) {
                JsysTitulos t = list.get(masterTable.convertRowIndexToModel(selected[idx]));
                String tipoPagamento = t.getTipoPagamento() == null ? "" : t.getTipoPagamento().substring(0, 2);
                if (Validar.isNotNullOrWhite(tipoPagamento)) {
                    t.setIdcupom(tipoPagamento);
                    br.sql.acesso.ConnectionFactory.update(t);
                    list.set(masterTable.convertRowIndexToModel(selected[idx]), t);
                    refreshButtonActionPerformed(null);
                } else {
                    JOptionPane.showMessageDialog(this, "Selecione uma Forma de Pagamento", "Aviso", JOptionPane.INFORMATION_MESSAGE);
                }
            }

        }
    }//GEN-LAST:event_saveButtonActionPerformed

    private void cartaoJChekBoxItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cartaoJChekBoxItemStateChanged
        bandeiraOpejComboBox.setSelectedIndex(5);
        cnpjPreatadoraJtextField.setText("");
    }//GEN-LAST:event_cartaoJChekBoxItemStateChanged

